#include <iostream>#include <vector>#include "stl_iterator.h"using std::cout;using std::cin;using std::endl;using std::vector;int main(){	vector<int> V(10);	for(int i=0;i!=10;i++)	{		V[i]=i;	}	typedef vector<int>::pointer vptr;	vptr p=&V.front();	//实例化一个vector<int>的iterator类	typedef numb::_normal_iterator<vptr,vector<int>> iterator;	//为这个vector构造一个迭代器	iterator i(p);	iterator j=i;	cout<<"j=i  : "<<*j<<endl;	//测试迭代器各项功能	//reference	cout<<"*i   : "<<*i<<endl;	//i++,i--,++i,--i	cout<<"i++  : "<<*(i++)<<endl;	cout<<"i--  : "<<*(i--)<<endl;	cout<<"++i  : "<<*(++i)<<endl;	cout<<"--i  : "<<*(--i)<<endl;	//operator  []	cout<<"[]   : "<<i[7]<<endl;	//operator  +=,-=,+,-	cout<<"i    : "<<*i<<endl;	cout<<"+=   : "<<*(i+=5)<<endl;	cout<<"-=   : "<<*(i-=5)<<endl;	i=j;        	cout<<"+    :  "<<*(i+5)<<endl;	i+=5;       	cout<<"-    :  "<<*(i-5)<<endl;	return 0;}